function checkAnswer() {
// Set the vaue of the underlined word
partOfSpeech = 'adjective';

// Get the user's input value
userAnswer = document.getElementById('userAnswer').value;

// Compare the user's answer to the correct answer
if (partOfSpeech == userAnswer) {
    // Log a message to make sure your logic works
    console.log('They got it right!');

    // Flash a message to the user letting them know it's right
    message = document.getElementById('message');
    message.innerHTML = 'That is the correct answer!';

} else {
    // Log a message to make sure your logic works
    console.log('Incorrect');

    // Flash a message to the user letting them know it's wrong
    message = document.getElementById('message');
    message.innerHTML = 'That is unfortunate.';
  }
}
